sql.create_images_idx:
CREATE INDEX idx_images ON images(
    path, file
);
CREATE INDEX idx_images_path ON images(
    path
);

sql._create_meta_idx:
CREATE INDEX idx_meta_file ON meta( path, file );
CREATE INDEX idx_meta_path ON meta( path );
CREATE INDEX idx_meta_exif ON meta( exif );
CREATE INDEX idx_meta_iptc ON meta( iptc );
